import React, {useState} from "react";
import './Post.css';
import Axios from 'axios';
import Navbar from './Navbar';


export const Post = (props) => {
    const url = 'https://jsonplaceholder.typicode.com/posts';
  const [data, setData] = useState({
    name: " ",
    description: " ",
  });

  function handleSubmit(e) {
    e.preventDefault();
    Axios.post(url, data)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }

  function handleInputChange(e) {
    const { id, value } = e.target;
    setData((prevData) => ({
      ...prevData,
      [id]: value,
    }));
  }





    // const[name, setName] = useState('');
    // const[university, setUniversity] = useState('');
    // const[course, setCourse] = useState('');
    // const[selectedFile, setSelectedFile] = useState('');

    // const handleSubmit = (e) => {
    //     e.preventDefault();
    // }
    return(
        // body
        <div>
            {/* top bar */}
            <Navbar/>
            <div>
                <form className="upload-form" onSubmit={handleSubmit}>
                    <div className="flex-container">
                    <div className="flex-child">
                        <label className="upload-labels" for="name">Post Name:</label>
                        <br/>
                        <input value={data.name} onChange={handleInputChange} type="text" id="name" className="upload-input"/>
                        <br/>
                        <br/>
                        <label className="upload-labels description-label" for="course">Description:</label>
                        <br/>
                        <textarea value={data.name} className="description"  onChange={handleInputChange} cols="60" rows="10"></textarea>
                        <br/>
                        <br/>
                        {/* <input value={course} onChange={(e)=> setCourse(e.target.value)} type="text" /> */}
                        <button className="button" type="submit">Post</button>
                    </div>

                    <div className="flex-child">

                    </div>
                    </div>
                </form>
            </div>

        </div>
    )
}
export default Post;
