import React, {useState, useEffect} from "react";
import './Home.css';
import Navbar from './Navbar';
import Axios from 'axios';


export const Home = (props) => {
    const[name, setName] = useState('');
    const[postList, setPostList] = useState([]);

    useEffect(()=> {
        Axios.get('http://localhost:3001/getposts').then((response)=>{
            setPostList(response.data)
        })
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        Axios.get('http://localhost:3001/searchpost', {
            name: name
        }).then(()=>{
            console.log("success");
        })
        useEffect(()=> {
            Axios.get('http://localhost:3001/getposts', {
                name: name
            }).then((response)=>{
                console.log("success");
                setPostList(response.data);
            })

        }, []);

    }



    return(
        // body
        <div>
             {/* top bar */}
             <Navbar/>
            <div>
                <form className="upload-form" onSubmit={handleSubmit}>
                    <div className="flex-container">
                    <div className="flex-child">
                        <label className="upload-labels" for="name">Let's Look Up Some Chatter!</label>
                        <br/>
                        <input value={name} onChange={(e)=> setName(e.target.value)} type="text" id="name" className="upload-input" placeholder="Post Name"/>
                        <br/>
                        <button className="button" type="submit">Search</button>
                    </div>

                    </div>

                    {postList.map((val)=>{
                    return <div className ='card-container'>
                        <div className ="card-content">
                            <div className = "card-title">
                                <h3>{val.name}</h3>
                            </div>

                            <div className="card-body">
                                <p>{val.description}</p>
                            </div>
                        </div>
                    </div>
                })}
                </form>


                <br/>
            </div>

        </div>
    )
}
export default Home;
